import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import { motion } from 'framer-motion';

const HowWeDoIt = () => {
  const [activeSection, setActiveSection] = useState(0);
  const navigate = useNavigate();

  // Define section titles
  const sectionTitles = [
    'Overview',
    'The Science Behind Afiniti',
    'Data Integration',
    'Pattern Recognition',
    'Real-Time Application',
    'Results & Impact'
  ];

  useEffect(() => {
    const observerOptions = {
      threshold: 0,
      rootMargin: "-80px 0px -80px 0px" // Adjust based on header height
    };

    const sectionObserver = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          const sectionHeader = entry.target;
          const sectionIndex = parseInt(sectionHeader.dataset.index);
          setActiveSection(sectionIndex);
        }
      });
    }, observerOptions);

    // Observe section h1 headings instead of sections
    const sectionHeaders = document.querySelectorAll('.section-header');
    sectionHeaders.forEach(header => {
      sectionObserver.observe(header);
    });

    return () => {
      sectionHeaders.forEach(header => {
        sectionObserver.unobserve(header);
      });
    };
  }, []);

  const handleBackToHome = (e) => {
    e.preventDefault();
    navigate('/', { state: { slideDirection: 'left' } });
  };

  const handleSectionChange = (index) => {
    setActiveSection(index);
    const targetSection = document.querySelector(`[data-section-index="${index}"]`);
    targetSection?.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <motion.div
      initial={{ x: '100%' }}
      animate={{ x: 0 }}
      exit={{ x: '-100%' }}
      transition={{ type: 'tween', duration: 0.5 }}
      className="h-screen overflow-y-auto overflow-x-hidden"
    >
      <div className="fixed top-0 left-0 right-0 z-50">
        <Navbar />
      </div>

      {/* First Section - Dark Blue Background */}
      <section className="relative min-h-screen bg-[#00264D] flex flex-col items-center justify-center">
        {/* Back to Home Link */}
        <h3 
          onClick={handleBackToHome}
          className="fixed top-32 left-20 text-white text-lg cursor-pointer hover:text-green-300 transition-colors duration-300 flex items-center gap-2 group z-40"
        >
          <span className="transform group-hover:-translate-x-2 transition-transform duration-300">←</span>
          Back to Home
        </h3>

        {/* Scroll Indicator */}
        <div className="absolute left-1/2 bottom-1 -translate-x-1/2 flex flex-col items-center gap-2">
          <div className="w-[1px] h-24 bg-white/20 relative overflow-hidden">
            <motion.div 
              className="w-full h-full bg-white"
              animate={{
                y: ['-100%', '100%'],
              }}
              transition={{
                repeat: Infinity,
                duration: 1.5,
                ease: 'linear',
              }}
            />
          </div>
        </div>
      </section>

      {/* White Background Sections */}
      <div className="relative bg-white">
        {/* Fixed Header - Only shows after blue section */}
        <div className="sticky top-0 z-40 bg-white py-8">
          <div className="text-center space-y-4">
            <motion.h1 
              key={activeSection}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.3 }}
              className="text-4xl font-serif text-black"
            >
              {sectionTitles[activeSection]}
            </motion.h1>
            <div className="flex justify-center gap-3">
              {sectionTitles.map((_, index) => (
                <motion.div
                  key={index}
                  className={`w-2 h-2 rounded-full cursor-pointer transition-all duration-300`}
                  animate={{
                    backgroundColor: index === activeSection ? '#ec4899' : '#d1d5db',
                    scale: index === activeSection ? 1.2 : 1
                  }}
                  transition={{ duration: 0.3 }}
                  onClick={() => handleSectionChange(index)}
                />
