import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
// import Navbar from '../components/Navbar'; // <--- REMOVE THIS IMPORT
import Footer from '../components/Footer';
import { motion } from 'framer-motion';
// import Lenis from 'lenis'; // <--- REMOVE THIS IMPORT if Lenis is initialized in App.js

const HowWeDoIt = ({ showProcessSummary = true }) => {
  const [activeSection, setActiveSection] = useState(0);
  const [showHeader, setShowHeader] = useState(false);
  // const [showNavbar, setShowNavbar] = useState(true); // <--- REMOVE OR RE-EVALUATE (Navbar is now in App.js)
  const [isOpen, setIsOpen] = useState(false); // Only relevant if controlling an internal menu
  const [expandedSections, setExpandedSections] = useState({});
  const [currentSlide, setCurrentSlide] = useState(0);
  const lastScrollY = useRef(0);
  const blueSectionRef = useRef(null);
  const whiteBackgroundRef = useRef(null);
  const navigate = useNavigate();

  // Define section titles
  const sectionTitles = [
    'Overview',
    'The Science Behind Afiniti',
    'Data Integration',
    'Pattern Recognition',
    'Results & Impact',
    'Process Summary',
    'Afiniti University'
  ];

  const processSteps = [ /* ... your process steps ... */ ];

  // Adjust sectionTitles based on the prop
  const visibleSectionTitles = showProcessSummary
    ? sectionTitles
    : sectionTitles.filter(title => title !== 'Process Summary');

  // REMOVE THE LENIS INITIALIZATION useEffect FROM HERE
  // It's now in App.js to control the whole document.

  // Keep these useEffects for section observation and sticky header logic within THIS component
  useEffect(() => {
    const observerOptions = {
      threshold: 0.15,
      rootMargin: "0px 0px -80% 0px"
    };

    const sectionObserver = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          const sectionIndex = parseInt(entry.target.dataset.sectionIndex);
          let actualSectionIndex = sectionIndex;
          if (!showProcessSummary && sectionIndex > 5) {
            actualSectionIndex = sectionIndex - 1;
          }
          setActiveSection(actualSectionIndex);

          // Logic for the *local* floating header
          // Assuming blueSectionRef marks the first section (index 0)
          if (blueSectionRef.current && entry.target === blueSectionRef.current) {
            setShowHeader(false); // Hide the floating header when in the blue section
          } else {
            setShowHeader(true); // Show the floating header when past the blue section
          }
        }
      });
    }, observerOptions);

    const sections = document.querySelectorAll('.content-section');
    sections.forEach((section) => {
      sectionObserver.observe(section);
    });

    // Also observe the blue section itself
    if (blueSectionRef.current) {
        sectionObserver.observe(blueSectionRef.current);
    }

    return () => {
      sections.forEach(section => {
        sectionObserver.unobserve(section);
      });
      if (blueSectionRef.current) {
        sectionObserver.unobserve(blueSectionRef.current);
      }
    };
  }, [showProcessSummary]); // Add showProcessSummary to dependencies

  useEffect(() => {
    const headerObserver = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setShowHeader(false); // Hide the fixed header when blue section is in view
          // setShowNavbar(true); // This state no longer controls the main App.js Navbar
        } else {
          setShowHeader(true); // Show the fixed header when blue section is scrolled past
          // setShowNavbar(false); // This state no longer controls the main App.js Navbar
        }
      },
      {
        threshold: 0.01,
        rootMargin: "-10px 0px 0px 0px"
      }
    );

    if (blueSectionRef.current) {
      headerObserver.observe(blueSectionRef.current);
    }

    return () => headerObserver.disconnect();
  }, []);

  const handleBackToHome = (e) => {
    e.preventDefault();
    navigate('/', { state: { slideDirection: 'left' } });
  };

  const handleSectionChange = (index) => {
    let targetIndex = index;
    if (!showProcessSummary && index >= 5) {
      targetIndex = index + 1;
    }
    setActiveSection(index);
    const targetSection = document.querySelector(`[data-section-index="${targetIndex}"]`);
    if (targetSection) {
      const headerHeight = 80; // Adjust if your main Navbar/fixed header is taller
      // Use Lenis to scroll if available, otherwise fallback to native
      if (window.lenis) { // Check if Lenis instance was attached to window in App.js
        window.lenis.scrollTo(targetSection, { offset: -headerHeight, duration: 1.2, easing: (t) => Math.min(1, 1.001 - Math.pow(2, -10 * t)) });
      } else {
        // Fallback to native scroll if Lenis is not available
        const targetPosition = targetSection.getBoundingClientRect().top + window.pageYOffset - headerHeight;
        window.scrollTo({
          top: targetPosition,
          behavior: 'smooth'
        });
      }

      if (isOpen) { // If this relates to a local menu, keep it
        toggleMenu();
      }
    }
  };

  const toggleMenu = () => {
    setIsOpen(!isOpen);
    // setShowNavbar(!showNavbar); // <--- REMOVE THIS, as Navbar is managed in App.js
  };

  const toggleSection = (sectionId) => {
    setExpandedSections(prev => ({
      ...prev,
      [sectionId]: !prev[sectionId]
    }));
  };

  const handleNextSlide = () => {
    setCurrentSlide(prev => (prev === 3 ? 0 : prev + 1));
  };

  const handlePrevSlide = () => {
    setCurrentSlide(prev => (prev === 0 ? 3 : prev - 1));
  };

  return (
    <>
      {/* This motion.div is now the main content container for HowWeDoIt page */}
      <motion.div
        initial={{ x: '100%' }}
        animate={{ x: 0 }}
        exit={{ x: '-100%' }}
        transition={{ type: 'tween', duration: 0.5 }}
        className="overflow-x-hidden" // Keep this to prevent horizontal scrollbars from animation
      >
        {/* First Section - Dark Blue Background */}
        <section
          ref={blueSectionRef}
          className="relative min-h-screen bg-[#070c24] flex flex-col items-center justify-center"
        >
          {/* REMOVE the Navbar rendering inside this section */}
          {/* {showNavbar && (
            <div className="absolute top-0 left-0 right-0 z-[1010]">
              <Navbar />
            </div>
          )} */}

          <h3
            onClick={handleBackToHome}
            className="fixed bottom-40 left-8 md:left-24 text-white text-lg cursor-pointer hover:text-green-300 transition-colors duration-300 flex items-center gap-2 group z-40"
          >
            <span className="transform group-hover:-translate-x-2 transition-transform duration-300">‚Üê</span>
            Back to Home
          </h3>

          <h1 className='fixed bottom-24 left-4 md:left-24 text-white text-3xl font-["Josefin_Sans"] font-light'>
            HOW WE DO IT
          </h1>
          <div className="absolute left-1/2 bottom-1 -translate-x-1/2 flex flex-col items-center gap-2">
            <div className="w-[1px] h-24 bg-white/20 relative overflow-hidden">
              <motion.div
                className="w-full h-full bg-white"
                animate={{
                  y: ['-100%', '100%'],
                }}
                transition={{
                  repeat: Infinity,
                  duration: 1.5,
                  ease: 'linear',
                }}
              />
            </div>
          </div>
        </section>

        {/* White Background Sections */}
        <div ref={whiteBackgroundRef} className="relative bg-white">
          {/* This sticky header is local to HowWeDoIt, so it can stay here */}
          <div
            className={`sticky top-0 z-30 bg-white py-4 transition-opacity duration-300
            ${showHeader ? 'opacity-100' : 'opacity-0 pointer-events-none'}`}
          >
            {/* Header Title - Middle */}
            <motion.h1
              key={activeSection}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.3 }}
              className="text-4xl font-['Josefin_Sans'] text-black text-center mb-4 font-light"
            >
              {visibleSectionTitles[activeSection]}
            </motion.h1>

            {/* Navigation dots - Bottom */}
            <div className="flex justify-center gap-2">
              {visibleSectionTitles.map((_, index) => (
                <motion.div
                  key={index}
                  className={`w-1.5 h-1.5 rounded-full cursor-pointer transition-all duration-300`}
                  animate={{
                    backgroundColor: index === activeSection ? '#ec4899' : '#d1d5db',
                    scale: index === activeSection ? 1.2 : 1
                  }}
                  transition={{ duration: 0.3 }}
                  onClick={(e) => {
                    e.preventDefault();
                    handleSectionChange(index);
                  }}
                />
              ))}
            </div>
          </div>

          {/* Content Sections */}
          {/* Ensure these sections are tall enough to create scrollable content */}
          <section data-section-index="0" className="content-section min-h-screen relative pt-1"> {/* ... */} </section>
          <section data-section-index="1" className="content-section min-h-screen relative pt-18"> {/* ... */} </section>
          <section data-section-index="2" className="content-section min-h-screen relative pt-19"> {/* ... */} </section>
          <section data-section-index="3" className="content-section min-h-screen relative pt-18 bg-pink-0"> {/* ... */} </section>
          <section data-section-index="4" className="content-section min-h-screen relative pt-18"> {/* ... */} </section>
          {showProcessSummary && (
            <section data-section-index="5" className="content-section h-auto relative pt-18 bg-[#f0f0f0] py-8"> {/* ... */} </section>
          )}
          <section data-section-index={showProcessSummary ? "6" : "5"} className="content-section min-h-screen relative py-4 bg-white pt-12"> {/* ... */} </section>

          {/* Footer Section */}
          <Footer />
        </div>
      </motion.div>
    </>
  );
};

export default HowWeDoIt;
