import React, { createContext, useContext, useEffect, useState } from 'react';
import Lenis from '@studio-freight/lenis';

export const LenisContext = createContext(null);

export const LenisProvider = ({ children }) => {
  const [lenis, setLenis] = useState(null);

  useEffect(() => {
    console.log('Initializing Lenis...');
    const instance = new Lenis({
      duration: 1.2,
      easing: (t) => Math.min(1, 1.001 - Math.pow(2, -10 * t)),
      orientation: 'vertical',
      gestureOrientation: 'vertical',
      smoothWheel: true,
      wheelMultiplier: 1,
      smoothTouch: false,
      touchMultiplier: 2,
    });

    setLenis(instance);

    // Add scroll listener to verify Lenis is working
    instance.on('scroll', ({ scroll, limit, velocity, direction, progress }) => {
      console.log('Lenis scroll:', { scroll, limit, velocity, direction, progress });
    });

    function raf(time) {
      instance?.raf(time);
      requestAnimationFrame(raf);
    }

    requestAnimationFrame(raf);
    console.log('Lenis initialized successfully');

    return () => {
      console.log('Cleaning up Lenis...');
      instance?.destroy();
    };
  }, []);

  return (
    <LenisContext.Provider value={lenis}>
      {children}
    </LenisContext.Provider>
  );
};

export const useLenis = () => {
  const lenis = useContext(LenisContext);
  if (!lenis) {
    console.warn('useLenis must be used within LenisProvider');
    return null;
  }
  return lenis;
};
