import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Loader from './components/Loader';
import Home from './pages/Home';
import CustomCursor from './components/Customcursor';
import HowWeDoIt from './pages/how-we-do-it';

const App = () => {
  const [loading, setLoading] = useState(true);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    // Check if the device is mobile/touch
    const checkMobile = () => {
      const isTouchDevice = 'ontouchstart' in window || navigator.maxTouchPoints > 0;
      setIsMobile(isTouchDevice || window.innerWidth <= 768);
    };

    checkMobile();
    window.addEventListener('resize', checkMobile);

    // Reduce loader time in production
    const timer = setTimeout(() => {
      setLoading(false);
    }, process.env.NODE_ENV === 'production' ? 1500 : 3500);
    
    return () => {
      clearTimeout(timer);
      window.removeEventListener('resize', checkMobile);
    };
  }, []);

  return (
    <Router>
      {/* Only show custom cursor on non-mobile devices */}
      {!isMobile && <CustomCursor />}
      
      {loading ? (
        <Loader />
      ) : (        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/how-we-do-it" element={<HowWeDoIt />} />
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      )}
    </Router>
  );
};

export default App;
