import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import { motion } from 'framer-motion';

const HowWeDoIt = () => {
  const [scrollY, setScrollY] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <motion.div
      initial={{ x: '100%' }}
      animate={{ x: 0 }}
      exit={{ x: '100%' }}
      transition={{ type: 'tween', duration: 0.5 }}
      className="relative min-h-[200vh]"
    >
      <Navbar />
      
      {/* First Section - Dark Blue Background */}
      <section className="relative h-screen bg-[#00264D] flex flex-col justify-end">
        {/* Scroll Indicator */}
        <div className="absolute left-1/2 bottom-12 -translate-x-1/2 flex flex-col items-center gap-2">
          <div className="w-[2px] h-16 bg-white/20 relative overflow-hidden">
            <motion.div 
              className="w-full h-full bg-white"
              animate={{
                y: ['-100%', '100%'],
              }}
              transition={{
                repeat: Infinity,
                duration: 1.5,
                ease: 'linear',
              }}
            />
          </div>
          <span className="text-white/60 text-sm">Scroll Down</span>
        </div>

        {/* Bottom Left Title */}
        <h1 className="text-white text-5xl font-bold p-20">
          How We Do It
        </h1>
      </section>

      {/* Second Section - White Background */}
      <section className="min-h-screen bg-white">
        {/* Add your content for the white section here */}
      </section>
    </motion.div>
  );
};

export default HowWeDoIt;
