import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import { motion } from 'framer-motion';

const HowWeDoIt = () => {
  const [scrollY, setScrollY] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleBackToHome = (e) => {
    e.preventDefault();
    // Add a custom flag to indicate direction for the home page animation
    navigate('/', { state: { slideDirection: 'left' } });
  };
  return (
    <motion.div
      initial={{ x: '100%' }}
      animate={{ x: 0 }}
      exit={{ x: '-100%' }} // Exit to the left when going back
      transition={{ type: 'tween', duration: 0.5 }}
      className="h-screen overflow-y-auto overflow-x-hidden"
    >
      <div className="fixed top-0 left-0 right-0 z-50">
        <Navbar />
      </div>      {/* First Section - Dark Blue Background */}
      <section className="relative min-h-screen bg-[#00264D] flex flex-col justify-between pt-32">
        {/* Back to Home Link */}
        <h3 
          onClick={handleBackToHome}
          className="fixed top-32 left-20 text-white text-lg cursor-pointer hover:text-green-300 transition-colors duration-300 flex items-center gap-2 group z-40"
        >
          <span className="transform group-hover:-translate-x-2 transition-transform duration-300">‚Üê</span>
          Back to Home
        </h3>

    

        
      </section>

      {/* Second Section - White Background */}

      
      <section className="min-h-screen bg-white px-20 py-32">
    
        {/* Video Container */}
        <div className="w-full max-w-3xl mx-auto aspect-video bg-gray-100 mt-20 rounded-lg overflow-hidden">
          <video 
            className="w-full h-full object-cover"
            controls
            src="https://res.cloudinary.com/ddzllbqlv/video/upload/v1748342620/zjtzamruh1ztnsjntam7.mp4"
          >
            Your browser does not support the video tag.
          </video>
        </div>
      </section>
    </motion.div>
  );
};

export default HowWeDoIt;
