import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import { motion, AnimatePresence } from 'framer-motion';

const HowWeDoIt = () => {
  const [activeSection, setActiveSection] = useState(0);
  const [headerHeight, setHeaderHeight] = useState(0);
  const navigate = useNavigate();
  const headerRef = useRef(null);

  // Define section titles
  const sectionTitles = [
    'Overview',
    'The Science Behind Afiniti',
    'Data Integration',
    'Pattern Recognition',
    'Real-Time Application',
    'Results & Impact'
  ];

  useEffect(() => {
    // Get header height for precise calculations
    if (headerRef.current) {
      setHeaderHeight(headerRef.current.offsetHeight);
    }

    const handleScroll = () => {
      if (!headerRef.current) return;
      
      // Get the sticky header position
      const headerRect = headerRef.current.getBoundingClientRect();
      const headerBottom = headerRect.bottom;
      
      const sections = document.querySelectorAll('.content-section');
      let currentSection = 0;
      
      // Check each section to see which one's heading is closest to passing the header
      sections.forEach((section, index) => {
        const sectionHeading = section.querySelector('.section-heading');
        
        if (sectionHeading) {
          const headingRect = sectionHeading.getBoundingClientRect();
          const headingTop = headingRect.top;
          
          // If the heading has passed the header bottom, this section is active
          if (headingTop <= headerBottom + 10) { // Small offset for better UX
            currentSection = index;
          }
        }
      });
      
      if (currentSection !== activeSection) {
        setActiveSection(currentSection);
      }
    };

    // Add scroll listener
    window.addEventListener('scroll', handleScroll);
    
    // Initial check
    setTimeout(handleScroll, 100);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [activeSection]);

  const handleBackToHome = (e) => {
    e.preventDefault();
    navigate('/', { state: { slideDirection: 'left' } });
  };

  const handleSectionChange = (index) => {
    const targetSection = document.querySelector(`[data-section-index="${index}"]`);
    if (targetSection) {
      const sectionHeading = targetSection.querySelector('.section-heading');
      if (sectionHeading && headerRef.current) {
        // Calculate the position to scroll to
        const headerHeight = headerRef.current.offsetHeight;
        const headingOffset = sectionHeading.offsetTop - headerHeight - 10; // Small offset
        window.scrollTo({
          top: headingOffset,
          behavior: 'smooth'
        });
      }
    }
  };

  return (
    <motion.div
      initial={{ x: '100%' }}
      animate={{ x: 0 }}
      exit={{ x: '-100%' }}
      transition={{ type: 'tween', duration: 0.5 }}
      className="h-screen overflow-y-auto overflow-x-hidden"
    >
      <div className="fixed top-0 left-0 right-0 z-50">
        <Navbar />
      </div>

      {/* First Section - Dark Blue Background */}
      <section className="relative min-h-screen bg-[#00264D] flex flex-col items-center justify-center">
        {/* Back to Home Link */}
        <h3 
          onClick={handleBackToHome}
          className="fixed top-32 left-20 text-white text-lg cursor-pointer hover:text-green-300 transition-colors duration-300 flex items-center gap-2 group z-40"
        >
          <span className="transform group-hover:-translate-x-2 transition-transform duration-300">←</span>
          Back to Home
        </h3>

        {/* Scroll Indicator */}
        <div className="absolute left-1/2 bottom-8 -translate-x-1/2 flex flex-col items-center gap-2">
          <div className="w-[1px] h-24 bg-white/20 relative overflow-hidden">
            <motion.div 
              className="w-full h-full bg-white"
              animate={{
                y: ['-100%', '100%'],
              }}
              transition={{
                repeat: Infinity,
                duration: 1.5,
                ease: 'linear',
              }}
            />
          </div>
        </div>
      </section>

      {/* White Background Sections */}
      <div className="relative bg-white">
        {/* Fixed Header - Only shows after blue section */}
        <div ref={headerRef} className="sticky top-0 z-40 bg-white">
          <div className="py-6">
            <div className="text-center">
              <AnimatePresence mode="wait">
                <motion.h1 
                  key={activeSection}
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -10 }}
                  transition={{ duration: 0.2 }}
                  className="text-3xl font-medium text-gray-900"
                >
                  {sectionTitles[activeSection]}
                </motion.h1>
              </AnimatePresence>
            </div>
          </div>
        </div>

        {/* Content Sections */}
        {/* Section 1: Overview */}
        <section data-section-index="0" className="content-section relative">
          <div className="min-h-screen px-6 py-20">
            <div className="max-w-4xl mx-auto">
              {/* Section Heading - This is what triggers the header change */}
              <h2 className="section-heading text-4xl font-medium text-gray-900 mb-12">
                {sectionTitles[0]}
              </h2>
              
              <div className="space-y-8 mb-20">
                <p className="text-gray-600 text-lg leading-relaxed">
                  Afiniti Enterprise Behavioral Pairing™ discovers and predicts patterns of interpersonal behavior to optimally pair customers with agents.
                </p>

                <p className="text-gray-600 text-lg leading-relaxed">
                  With over 150 patents, Afiniti's technology examines data and commercially available information tied to customer identity to determine patterns of successful behavioral interactions and applies these patterns in real time to drive improvements in health, enterprise profitability, and customer satisfaction.
                </p>
              </div>

              <div className="w-full aspect-video bg-gray-100 rounded-lg overflow-hidden">
                <video 
                  className="w-full h-full object-cover"
                  controls
                  src="https://res.cloudinary.com/ddzllbqlv/video/upload/v1748342620/zjtzamruh1ztnsjntam7.mp4"
                >
                  Your browser does not support the video tag.
                </video>
              </div>
            </div>
          </div>
        </section>

        {/* Section 2: The Science Behind Afiniti */}
        <section data-section-index="1" className="content-section relative">
          <div className="min-h-screen px-6 py-20">
            <div className="max-w-4xl mx-auto">
              <h2 className="section-heading text-4xl font-medium text-gray-900 mb-12">
                {sectionTitles[1]}
              </h2>
              
              <div className="space-y-8 mb-20">
                <p className="text-gray-600 text-lg leading-relaxed">
                  In this process we combine interaction-level outcomes data from our clients with hundreds of internal and external databases through highly complex data joins in order to build a rich contextual data source for each interaction.
                </p>

                <p className="text-gray-600 text-lg leading-relaxed">
                  Using this data, Afiniti deploys specialized machine learning techniques to identify behavioral patterns in customer-representative interactions that lead to success.
                </p>
              </div>

              <div className="w-full aspect-video bg-gray-100 rounded-lg overflow-hidden">
                <video 
                  className="w-full h-full object-cover"
                  controls
                  src="path-to-your-second-video.mp4"
                >
                  Your browser does not support the video tag.
                </video>
              </div>
            </div>
          </div>
        </section>

        {/* Section 3: Data Integration */}
        <section data-section-index="2" className="content-section relative">
          <div className="min-h-screen px-6 py-20">
            <div className="max-w-4xl mx-auto">
              <h2 className="section-heading text-4xl font-medium text-gray-900 mb-12">
                {sectionTitles[2]}
              </h2>
              
              <div className="space-y-8 mb-20">
                <p className="text-gray-600 text-lg leading-relaxed">
                  Our data integration process seamlessly combines multiple data sources to create comprehensive behavioral profiles that drive intelligent pairing decisions.
                </p>

                <p className="text-gray-600 text-lg leading-relaxed">
                  By leveraging advanced data engineering techniques, we process millions of data points in real-time, ensuring that every customer interaction is optimized based on the most current and relevant information available.
                </p>
              </div>

              <div className="w-full aspect-video bg-gray-100 rounded-lg overflow-hidden">
                <video 
                  className="w-full h-full object-cover"
                  controls
                  src="path-to-your-third-video.mp4"
                >
                  Your browser does not support the video tag.
                </video>
              </div>
            </div>
          </div>
        </section>

        {/* Section 4: Pattern Recognition */}
        <section data-section-index="3" className="content-section relative">
          <div className="min-h-screen px-6 py-20">
            <div className="max-w-4xl mx-auto">
              <h2 className="section-heading text-4xl font-medium text-gray-900 mb-12">
                {sectionTitles[3]}
              </h2>
              
              <div className="space-y-8 mb-20">
                <p className="text-gray-600 text-lg leading-relaxed">
                  Our proprietary machine learning algorithms identify subtle patterns in human behavior that traditional systems miss, uncovering the hidden dynamics that make interactions successful.
                </p>

                <p className="text-gray-600 text-lg leading-relaxed">
                  Through continuous learning and adaptation, our models become increasingly sophisticated, recognizing complex behavioral patterns that predict positive outcomes across diverse industries and use cases.
                </p>
              </div>

              <div className="w-full aspect-video bg-gray-100 rounded-lg overflow-hidden">
                <video 
                  className="w-full h-full object-cover"
                  controls
                  src="path-to-your-fourth-video.mp4"
                >
                  Your browser does not support the video tag.
                </video>
              </div>
            </div>
          </div>
        </section>

        {/* Section 5: Real-Time Application */}
        <section data-section-index="4" className="content-section relative">
          <div className="min-h-screen px-6 py-20">
            <div className="max-w-4xl mx-auto">
              <h2 className="section-heading text-4xl font-medium text-gray-900 mb-12">
                {sectionTitles[4]}
              </h2>
              
              <div className="space-y-8 mb-20">
                <p className="text-gray-600 text-lg leading-relaxed">
                  Afiniti's technology operates in real-time, making split-second pairing decisions that maximize the potential for positive outcomes in every customer interaction.
                </p>

                <p className="text-gray-600 text-lg leading-relaxed">
                  Our platform seamlessly integrates with existing contact center infrastructure, applying behavioral insights at the moment of connection to ensure optimal pairings without disrupting operational flow.
                </p>
              </div>

              <div className="w-full aspect-video bg-gray-100 rounded-lg overflow-hidden">
                <video 
                  className="w-full h-full object-cover"
                  controls
                  src="path-to-your-fifth-video.mp4"
                >
                  Your browser does not support the video tag.
                </video>
              </div>
            </div>
          </div>
        </section>

        {/* Section 6: Results & Impact */}
        <section data-section-index="5" className="content-section relative">
          <div className="min-h-screen px-6 py-20">
            <div className="max-w-4xl mx-auto">
              <h2 className="section-heading text-4xl font-medium text-gray-900 mb-12">
                {sectionTitles[5]}
              </h2>
              
              <div className="space-y-8 mb-20">
                <p className="text-gray-600 text-lg leading-relaxed">
                  Organizations using Afiniti see measurable improvements in key business metrics, from increased sales and customer satisfaction to reduced churn and operational costs.
                </p>

                <p className="text-gray-600 text-lg leading-relaxed">
                  Our proven track record spans multiple industries, with Fortune 500 companies experiencing significant ROI through the power of behavioral pairing technology that transforms every interaction into an opportunity for success.
                </p>
              </div>

              <div className="w-full aspect-video bg-gray-100 rounded-lg overflow-hidden">
                <video 
                  className="w-full h-full object-cover"
                  controls
                  src="path-to-your-sixth-video.mp4"
                >
                  Your browser does not support the video tag.
                </video>
              </div>
            </div>
          </div>
        </section>
      </div>
    </motion.div>
  );
};

export default HowWeDoIt;
