import React, { useEffect, useState, useRef } from 'react';

// Mock Navbar component for demo
const Navbar = () => <div className="h-16 bg-transparent"></div>;

const HowWeDoIt = () => {
  const [activeSection, setActiveSection] = useState(0);
  const [showHeader, setShowHeader] = useState(false);
  const [showNavbar, setShowNavbar] = useState(false);
  const [isOpen, setIsOpen] = useState(false);
  const lastScrollY = useRef(0);
  const blueSectionRef = useRef(null);
  const whiteBackgroundRef = useRef(null);

  // Define section titles
  const sectionTitles = [
    'Overview',
    'The Science Behind Afiniti',
    'Data Integration',
    'Pattern Recognition',
    'Real-Time Application',
    'Results & Impact'
  ];

  useEffect(() => {
    const observerOptions = {
      threshold: 0.15,
      rootMargin: "0px 0px -80% 0px"
    };

    const sectionObserver = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          const sectionIndex = parseInt(entry.target.dataset.sectionIndex);
          setActiveSection(sectionIndex);
          // Show header when Overview section is reached
          if (sectionIndex === 0) {
            setShowHeader(true);
          }
        }
      });
    }, observerOptions);

    // Observe all sections
    const sections = document.querySelectorAll('.content-section');
    sections.forEach((section) => {
      sectionObserver.observe(section);
    });

    return () => {
      sections.forEach(section => {
        sectionObserver.unobserve(section);
      });
    };
  }, []);

  useEffect(() => {
    const headerObserver = new IntersectionObserver(
      ([entry]) => {
        // If blue section is intersecting, hide header
        if (entry.isIntersecting) {
          setShowHeader(false);
          return;
        }

        // When in white section, show header only when scrolling down
        const isScrollingDown = window.scrollY > lastScrollY.current;
        lastScrollY.current = window.scrollY;
        
        setShowHeader(isScrollingDown);
      },
      {
        threshold: 0,
      }
    );

    // Observe blue section
    if (blueSectionRef.current) {
      headerObserver.observe(blueSectionRef.current);
    }

    return () => headerObserver.disconnect();
  }, []);

  const handleBackToHome = (e) => {
    e.preventDefault();
    console.log('Navigate to home');
  };

  const handleSectionChange = (index) => {
    setActiveSection(index);
    const targetSection = document.querySelector(`[data-section-index="${index}"]`);
    targetSection?.scrollIntoView({ behavior: 'smooth' });
  };

  const toggleMenu = () => {
    setIsOpen(!isOpen);
    setShowNavbar(!showNavbar);
  };

  return (
    <div className="h-screen overflow-y-auto overflow-x-hidden">
      {/* Menu Overlay - FIXED */}
      <div
        className={`fixed inset-0 bg-white z-[1001] transform transition-transform duration-500 ease-in-out ${
          isOpen ? 'translate-x-0' : 'translate-x-full'
        }`}
      >
        <div className="flex items-center justify-center h-full px-5 sm:px-8 md:px-12">
          <div className="w-full max-w-6xl">
            <div className="flex flex-col items-center justify-center gap-4 sm:gap-6 md:gap-8 lg:gap-12">
              {sectionTitles.map((title, index) => (
                <button
                  key={title}
                  onClick={() => {
                    handleSectionChange(index);
                    toggleMenu();
                  }}
                  className={`relative px-6 py-4 sm:px-8 sm:py-5 md:px-10 md:py-6 rounded-full overflow-hidden group bg-transparent
                            transition-all duration-300 whitespace-nowrap
                            focus:outline-none focus:ring-2 focus:ring-green-400 focus:ring-opacity-50
                            hover:scale-105 active:scale-95 transform ${
                  isOpen 
                    ? 'translate-x-0 opacity-100' 
                    : 'translate-x-8 opacity-0'
                }`}
                  style={{
                    transitionDelay: isOpen ? `${index * 100}ms` : '0ms'
                  }}
                >
                  <span className="relative z-10 text-sm sm:text-base md:text-lg lg:text-xl font-normal 
                                transition-colors duration-300 text-black group-hover:text-green-600
                                text-center">
                    {title}
                  </span>
                </button>
              ))}
            </div>
          </div>
        </div>
      </div>

      <div className="fixed top-0 left-0 right-0 z-40">
        <Navbar />
      </div>

      {/* First Section - Dark Blue Background */}
      <section 
        ref={blueSectionRef}
        className="relative min-h-screen bg-blue-900 flex flex-col items-center justify-center"
      >
        {/* Back to Home Link */}
        <h3 
          onClick={handleBackToHome}
          className="fixed bottom-36 left-20 text-white text-lg cursor-pointer hover:text-green-300 transition-colors duration-300 flex items-center gap-2 group z-40"
        > 
          <span className="transform group-hover:-translate-x-2 transition-transform duration-300">←</span>
          Back to Home
        </h3>

        <h1 className="fixed bottom-20 left-20 text-white text-3xl font-light">
          HOW WE DO IT 
        </h1>
        
        {/* Scroll Indicator */}
        <div className="absolute left-1/2 bottom-1 -translate-x-1/2 flex flex-col items-center gap-2">
          <div className="w-[1px] h-24 bg-white/20 relative overflow-hidden">
            <div 
              className="w-full h-full bg-white animate-pulse"
            />
          </div>
        </div>
      </section>

      {/* White Background Sections */}
      <div ref={whiteBackgroundRef} className="relative bg-white">
       {/* Fixed Header */}
        <div 
          className={`sticky top-0 z-50 bg-white py-2 px-6 transition-opacity duration-300 
          ${showHeader ? 'opacity-100' : 'opacity-0 pointer-events-none'}`}
        >
          {/* Logo and Menu Bar - Top */}
          <div className="relative w-full h-3 mb-2 flex justify-between items-center top-6">
            {/* Logo */}
            <span className="text-4xl font-bold text-black ml-16"> 
              afiniti
            </span>

            {/* Hamburger Menu */}
            <button 
              className="flex flex-col justify-center items-center group p-2 relative
                        w-8 h-8 sm:w-10 sm:h-10 md:w-12 md:h-12
                        rounded-lg flex-shrink-0 mr-16 z-[1002]"
              onClick={toggleMenu}
              aria-label="Toggle menu"
              aria-expanded={isOpen}
            >
              <span
                className={`block rounded transition-all duration-300 ease-out
                          w-6 h-1 sm:w-8 sm:h-1 md:w-10 md:h-1
                          ${isOpen
                            ? 'rotate-45 translate-y-1 bg-green-900'
                            : 'bg-black group-hover:bg-green-600 -translate-y-1'
                          }`}
              />
              <span
                className={`block rounded transition-all duration-300 ease-out
                          w-6 h-1 sm:w-8 sm:h-1 md:w-10 md:h-1
                          ${isOpen
                            ? '-rotate-45 -translate-y-1 bg-green-900'
                            : 'bg-black group-hover:bg-green-600 translate-y-1'
                          }`}
              />
            </button>
          </div>

          {/* Header Title - Middle */}
          <h1 
            className="text-4xl text-black text-center mb-1 font-light"
          >
            {sectionTitles[activeSection]}
          </h1>

          {/* Navigation dots - Bottom */}
          <div className="flex justify-center gap-2 mb-1">
            {sectionTitles.map((_, index) => (
              <div
                key={index}
                className={`w-1.5 h-1.5 rounded-full cursor-pointer transition-all duration-300 ${
                  index === activeSection ? 'bg-pink-500 scale-125' : 'bg-gray-300'
                }`}
                onClick={() => handleSectionChange(index)}
              />
            ))}
          </div>
        </div>

        {/* Content Sections */}
        {/* Section 1: Overview */}
        <section data-section-index="0" className="content-section min-h-screen relative pt-1">
          <div className="flex-1 flex flex-col items-center justify-center px-6 pb-2">
            <h1 className="text-4xl text-pink-300 mb-20 top-4 font-light">Overview</h1>
            <h2 className="text-gray-400 text-xl font-light leading-relaxed max-w-2xl text-center mb-16">
              Afiniti Enterprise Behavioral Pairing™ discovers and predicts patterns of interpersonal behavior to optimally pair customers with agents.
            </h2>

            <p className="text-gray-700 max-w-2xl text-center leading-relaxed mb-16">
              With over 150 patents, Afiniti's technology examines data and commercially available information tied to customer identity to determine patterns of successful behavioral interactions and applies these patterns in real time to drive improvements in health, enterprise profitability, and customer satisfaction.
            </p>

            <div className="w-full max-w-xl h-[400px] mx-auto aspect-video bg-gray-100 rounded-lg overflow-hidden mb-4">
              <video 
                className="w-full h-full object-cover"
                controls
                src="https://res.cloudinary.com/ddzllbqlv/video/upload/v1748342620/zjtzamruh1ztnsjntam7.mp4"
              >
                Your browser does not support the video tag.
              </video>
            </div>
          </div>
        </section>

        {/* Section 2: The Science Behind Afiniti */}
        <section data-section-index="1" className="content-section min-h-screen relative pt-20">
          <h1 className="text-4xl font-serif text-pink-300 mb-16 text-center">The Science Behind Afiniti</h1>
          <div className="flex flex-col max-w-7xl mx-auto px-6 pb-20 gap-16">
            {/* First Row */}
            <div className="flex flex-row items-start justify-between gap-12">
              <div className="w-1/2 space-y-8">
                <div className="space-y-6">
                  <h2 className="text-2xl font-light text-gray-700">The Process</h2>
                  <p className="text-gray-600 text-lg leading-relaxed">
                    In this process we combine interaction-level outcomes data from our clients with hundreds of internal and external databases through highly complex data joins in order to build a rich contextual data source for each interaction. Using this data, Afiniti deploys specialized machine learning techniques to identify behavioral patterns in customer-representative interactions that lead to success.
                  </p>
                </div>
              </div>
              
              <div className="w-1/2">
                <div className="aspect-video bg-gray-100 rounded-lg overflow-hidden">
                  <video 
                    className="w-full h-full object-cover"
                    controls
                    src="path-to-your-second-video.mp4"
                    poster="/path-to-your-thumbnail.jpg"
                  >
                    Your browser does not support the video tag.
                  </video>
                </div>
              </div>
            </div>

            {/* Second Row */}
            <div className="flex flex-row items-start justify-between gap-12">
              <div className="w-1/2">
                <div className="aspect-video bg-gray-100 rounded-lg overflow-hidden">
                  <video 
                    className="w-full h-full object-cover"
                    controls
                    src="path-to-your-second-video-2.mp4"
                    poster="/path-to-your-thumbnail-2.jpg"
                  >
                    Your browser does not support the video tag.
                  </video>
                </div>
              </div>

              <div className="w-1/2 space-y-8">
                <div className="space-y-6">
                  <h2 className="text-2xl font-light text-gray-700">The Results</h2>
                  <p className="text-gray-600 text-lg leading-relaxed">
                    Through this innovative approach, we've achieved remarkable improvements in customer satisfaction and business outcomes. Our AI-driven pairing technology continuously learns and adapts, ensuring optimal matches between customers and agents while driving measurable value for our clients.
                  </p>
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* Section 3: Data Integration */}
        <section data-section-index="2" className="content-section min-h-screen relative pt-20">
          <div className="flex-1 flex flex-col items-center justify-center px-6 pb-20">
            <h1 className="text-4xl text-pink-300 mb-8">Data Integration</h1>
            <h2 className="text-gray-600 text-xl font-light leading-relaxed max-w-2xl text-center mb-16">
              Our data integration process seamlessly combines multiple data sources to create comprehensive behavioral profiles that drive intelligent pairing decisions.
            </h2>

            <p className="text-gray-700 max-w-2xl text-center leading-relaxed mb-16">
              By leveraging advanced data engineering techniques, we process millions of data points in real-time, ensuring that every customer interaction is optimized based on the most current and relevant information available.
            </p>

            <div className="w-full max-w-xl h-[400px] mx-auto aspect-video bg-gray-100 rounded-lg overflow-hidden mb-40">
              <video 
                className="w-full h-full object-cover"
                controls
                src="path-to-your-third-video.mp4"
              >
                Your browser does not support the video tag.
              </video>
            </div>
          </div>
        </section>

        {/* Section 4: Pattern Recognition */}
        <section data-section-index="3" className="content-section min-h-screen relative pt-20">
          <div className="flex-1 flex flex-col items-center justify-center px-6 pb-20">
            <h1 className="text-4xl text-pink-300 mb-8">Pattern Recognition</h1>
            <h2 className="text-gray-600 text-xl font-light leading-relaxed max-w-2xl text-center mb-16">
              Our proprietary machine learning algorithms identify subtle patterns in human behavior that traditional systems miss, uncovering the hidden dynamics that make interactions successful.
            </h2>

            <p className="text-gray-700 max-w-2xl text-center leading-relaxed mb-16">
              Through continuous learning and adaptation, our models become increasingly sophisticated, recognizing complex behavioral patterns that predict positive outcomes across diverse industries and use cases.
            </p>

            <div className="w-full max-w-xl h-[400px] mx-auto aspect-video bg-gray-100 rounded-lg overflow-hidden mb-40">
              <video 
                className="w-full h-full object-cover"
                controls
                src="path-to-your-fourth-video.mp4"
              >
                Your browser does not support the video tag.
              </video>
            </div>
          </div>
        </section>

        {/* Section 5: Real-Time Application */}
        <section data-section-index="4" className="content-section min-h-screen relative pt-20">
          <div className="flex-1 flex flex-col items-center justify-center px-6 pb-20">
            <h1 className="text-4xl text-pink-300 mb-8">Real-Time Application</h1>
            <h2 className="text-gray-600 text-xl font-light leading-relaxed max-w-2xl text-center mb-16">
              Afiniti's technology operates in real-time, making split-second pairing decisions that maximize the potential for positive outcomes in every customer interaction.
            </h2>

            <p className="text-gray-700 max-w-2xl text-center leading-relaxed mb-16">
              Our platform seamlessly integrates with existing contact center infrastructure, applying behavioral insights at the moment of connection to ensure optimal pairings without disrupting operational flow.
            </p>

            <div className="w-full max-w-xl h-[400px] mx-auto aspect-video bg-gray-100 rounded-lg overflow-hidden mb-40">
              <video 
                className="w-full h-full object-cover"
                controls
                src="path-to-your-fifth-video.mp4"
              >
                Your browser does not support the video tag.
              </video>
            </div>
          </div>
        </section>

        {/* Section 6: Results & Impact */}
        <section data-section-index="5" className="content-section min-h-screen relative pt-20">
          <div className="flex-1 flex flex-col items-center justify-center px-6 pb-20">
            <h1 className="text-4xl text-pink-300 mb-8">Results & Impact</h1>
            <h2 className="text-gray-600 text-xl font-light leading-relaxed max-w-2xl text-center mb-16">
              Organizations using Afiniti see measurable improvements in key business metrics, from increased sales and customer satisfaction to reduced churn and operational costs.
            </h2>

            <p className="text-gray-700 max-w-2xl text-center leading-relaxed mb-16">
              Our proven track record spans multiple industries, with Fortune 500 companies experiencing significant ROI through the power of behavioral pairing technology that transforms every interaction into an opportunity for success.
            </p>

            <div className="w-full max-w-xl h-[400px] mx-auto aspect-video bg-gray-100 rounded-lg overflow-hidden mb-40">
              <video 
                className="w-full h-full object-cover"
                controls
                src="path-to-your-sixth-video.mp4"
              >
                Your browser does not support the video tag.
              </video>
            </div>
          </div>
        </section>
      </div>
    </div>
  );
};

export default HowWeDoIt;
